"use strict";

$(document).ready(function () {
    var accordionItems = $(".templatemo-accordion > li > ul").hide();

    $(".templatemo-accordion > li > a").click(function () {
        var currentItem = $(this).next();

        if (!currentItem.hasClass("active")) {
            accordionItems.removeClass("active").slideUp();
            currentItem.addClass("active").slideDown();
        }

        return false;
    });

    $(".product-links-wap a").click(function () {
        var imageUrl = $(this).children("img").attr("src");
        $("#product-detail").attr("src", imageUrl);

        return false;
    });

    $("#btn-minus").click(function () {
        var value = $("#var-value").html();
        value = value === "1" ? value : parseInt(value) - 1;
        $("#var-value").html(value);
        $("#product-quanity").val(value);

        return false;
    });

    $("#btn-plus").click(function () {
        var value = $("#var-value").html();
        value++;
        $("#var-value").html(value);
        $("#product-quanity").val(value);

        return false;
    });

    $(".btn-size").click(function () {
        var size = $(this).html();
        $("#product-size").val(size);

        $(".btn-size").removeClass("btn-secondary").addClass("btn-success");
        $(this).removeClass("btn-success").addClass("btn-secondary");

        return false;
    });
});
